import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';

const userList = [
    {
        'id': 0,
        'name': 'Jack Wixom',
        'password': 'qHYgwUc@gmail.com'
    }, {
        'id': 1,
        'name': 'Janet Wise',
        'password': 'sROkowT@gmail.com'
    }
];

const favoriteList = [
    {
        'user': 'a',
        'room': 'aaaa'
    }
];

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            favoriteList: favoriteList
        }
    }

    componentDidMount() {
       this.getFavoriteList();
    }

    getFavoriteList = () => {
        axios.get('/pillow/favorite')
            .then(res => {
                this.setState({favoriteList: res.data});
                console.log(res);
            })
            .catch(e => console.log(e));
    }

    addFavorite = () => {
        const favoInfo = {
                user: 1,
                room: 4
        };
        const name = 'A';
        axios.post('/pillow/', name).then(res => {
            console.log(res);
        }).catch(e => console.log(e));
    }

    render() {
        return <div>
            <h1>Sign Up</h1>
            <div>Favorite List</div>
            <span>user_id room_id</span>
            {
                this.state.favoriteList.map(item => <div>
                    <span>{item.user} {item.room}</span>
                </div>)
            }
            <button onClick={() => this.addFavorite()}>
                add to favorite
            </button>
        </div>
    }
}

export default Signup;
